pages = [
    ('BungeeLayers', colorLayers),
    ('BungeeLayersRotated', colorLayers),
    ('Bungee-Regular', None),
    ('Bungee-Hairline', None),
    ('Bungee-Inline', None),
    ('Bungee-Outline', None),
    ('Bungee-Shade', None),

    ('BungeeLayers-Regular', None),
    ('BungeeLayers-Inline', None),
    ('BungeeLayers-Outline', None),
    ('BungeeLayers-Shade', None),

    ('BungeeLayersRotated-Regular', None),
    ('BungeeLayersRotated-Inline', None),
    ('BungeeLayersRotated-Outline', None),
    ('BungeeLayersRotated-Shade', None),
    
    ]

inch = 72
cellWidth = cellHeight = 40
mySize = 28

for page in pages:
    newPage(11*inch, 48*inch)
    fontFamily, layers = page
    if not layers:
        fontFamily, layerName = fontFamily.split('-')
        layers = [
            (layerName, (0, 0, 0))
            ]

    translate(30, height()-30)

    xoffset = 0
    yoffset = -cellHeight*1.5
    
    font('InputSans-Light', 9)
    text(page[0], (0, 0))

    fontSize(mySize)
    for u in unicodes:
        char = unichr(u)
        # now loop through each layer in the layer list
        for layer in layers:
            # split the tuple and set the font
            layerName, layerColor = layer
            font('%s-%s' % (fontFamily, layerName))
            # split the RGB value, and set the fill
            r, g, b = layerColor
            fill(r, g, b)
            # draw the text
            textBox(char, (xoffset, yoffset, cellWidth, cellHeight), align="center")
        xoffset += cellWidth
        if xoffset > width()-cellWidth*2:
            xoffset = 0
            yoffset -= cellHeight
            